const cheatsAdmin = Game.cheatsAdmin

cheatsAdmin.createCommand({
    name: 'shutdown',
    ownerOnly: true,
    autoGetTarget: false
}, async(cmd) => {
    await Game.setDataLoaded().catch(() => {})
    
    // shutdown should only be used by the set owner.
    if (Game.serverSettings.local || (Game.setData.creator.id === cmd.caller.userId)) {
        cheatsAdmin.logAudit(cmd)
        Game.shutdown(0)
    }
})